import React, { FunctionComponent, useState } from 'react';
import Pokemon from '../models/pokemon';
import { useHistory } from 'react-router-dom';
import formatType from '../helpers/format-type';
import PokemonService from '../services/pokemon-service';

type Props = {
    pokemon: Pokemon,
    isEditForm: boolean
};

// sera lié au champs que l'on définira mais sont optionnels
type Field = {
    value?: any,
    error?: string,
    isValid?: boolean
}

// quand on défini un champs on lui attribut un field pour que chaque champs définient possèdent cela
type Form = {
    picture: Field,
    name: Field,
    hp: Field,
    cp: Field,
    types: Field
}

const PokemonForm: FunctionComponent<Props> = ({pokemon, isEditForm}) => {

    const [form, setForm] = useState<Form>({
        picture: {value: pokemon.picture},
        name: {value: pokemon.name, isValid: true},
        hp: {value: pokemon.hp, isValid: true},
        cp: {value: pokemon.cp, isValid: true},
        types: {value: pokemon.types, isValid: true}
    })

    const history = useHistory();

    const types: string[] = [
        'Plante', 'Feu', 'Eau', 'Insecte', 'Normal', 'Electrik',
        'Poison', 'Fée', 'Vol', 'Combat', 'Psy'
    ];

    // check si le pokemon possède un type: output boolean
    const hasType = (type: string): boolean => {
        // ajoute dans le form.types les types qui retournent true
        return form.types.value.includes(type);
    }

    // permet de modifier l'état des données du form dès que l'utilisateur modifie les données
    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const fieldName: string = e.target.name; // on repère le nom de l'input que l'utilisateur modifie
        const fieldValue: string = e.target.value; // on recup la valeur de cet input
        const newField: Field = {[fieldName]: {value: fieldValue}}; // on met ces 2 données dans un objet nommé newField

        setForm({...form, ...newField}); // on modifie l'état de la variable form avec 
    }

    // check et modifie les types du pokemon dès que l'utilisateur modifie un type
    const selectType = (type: string, e: React.ChangeEvent<HTMLInputElement>): void => {
        const checked = e.target.checked;
        let newField: Field;

        if (checked) {
            // Si l'utilisateur coche un type, on l'ajoute à la liste des types du pokemon
            const newTypes: string[] = form.types.value.concat([type]);
            newField = {value: newTypes}
        }
        else{
            // utilisateur décoche un type, on le retire de la liste des types du pokemon
            const newTypes: string[] = form.types.value.filter((currentType: string) => currentType !== type);
            newField = {value: newTypes}
        }

        setForm({...form, ...{types: newField}});
    }

    const handleSubmit = (e: React.FormEvent<HTMLElement>) => {
        e.preventDefault(); // on stop pour traiter nous meme les infos
        const isFormValid = validateForm(); // check si les données sont valident
        
        if (isFormValid) {
            // on met les valeurs soumise dans l'objet pokemon
            pokemon.picture = form.picture.value;
            pokemon.name = form.name.value;
            pokemon.hp = form.hp.value;
            pokemon.cp = form.cp.value;
            pokemon.types = form.types.value;

            // on check si c'est le formulaire d'edition ou d'ajout
            isEditForm ? updatePokemon() : addPokemon();
        }
    }

    const addPokemon = () => {
        console.log(pokemon);
        
        PokemonService.addPokemon(pokemon).then(() => history.push(`/pokemons`));
    }

    const updatePokemon = () => {
        PokemonService.updatePokemon(pokemon).then(() => history.push(`/pokemons/${pokemon.id}`));
    }

    const isAddForm = () => {
        return !isEditForm;
    }

    const validateForm = () => {
        let newForm: Form = form;

        // Validate url
        if (isAddForm()) {
            const start = "https://assets.pokemon.com/assets/cms2/img/pokedex/detail/";
            const end = ".png";

            if (!form.picture.value.startsWith(start) || !form.picture.value.endsWith(end)) { // check si l'url fourni par le user commence par la string start et fini par end
                const errorMsg: string = "L'url n'est pas valide.";
                const newField: Field = {value: form.picture.value, error: errorMsg, isValid: false};
                newForm = {...form, ...{picture: newField}};
            }
            else{
                const newField: Field = {value: form.picture.value, error: '', isValid: true};
                newForm = {...form, ...{picture: newField}};
            }
        }

        // validator name
        if (!/^[a-zA-Zàéè]{3,25}$/.test(form.name.value)) {
            console.log(form.name.value);
            
            const errorMsg: string = 'Le nom du pokemon est requis (1-25).';
            const newField: Field = {value: form.name.value, error: errorMsg, isValid: false};
            newForm = {...newForm, ...{name: newField}};
        } 
        else {
            const newField: Field = {value: form.name.value, error: '', isValid: true};
            newForm = {...newForm, ...{name: newField}};
        }

        // validator HP
        if (!/^[0-9]{1,3}$/.test(form.hp.value)) {
            const errorMsg: string = 'Les points de vie du pokemon sont compris entre 0 et 999.';
            const newField: Field = {value: form.hp.value, error: errorMsg, isValid: false};
            newForm = {...newForm, ...{hp: newField}};
        } 
        else {
            const newField: Field = {value: form.hp.value, error: '', isValid: true};
            newForm = {...newForm, ...{hp: newField}};
        }

        // validator CP
        if (!/^[0-9]{1,2}$/.test(form.cp.value)) {
            const errorMsg: string = 'Les dégats du pokemon sont compris entre 0 et 99.';
            const newField: Field = {value: form.cp.value, error: errorMsg, isValid: false};
            newForm = {...newForm, ...{cp: newField}};
        } 
        else {
            const newField: Field = {value: form.cp.value, error: '', isValid: true};
            newForm = {...newForm, ...{cp: newField}};
        }

        setForm(newForm);
        return newForm.name.isValid && newForm.hp.isValid && newForm.cp.isValid;
    }

    const isTypesValid = (type: string): boolean => {
        if (form.types.value.length === 1 && hasType(type)) {
            return false;
        }
        if (form.types.value.length >= 3 && !hasType(type)) {
            return false;
        }

        return true;
    }

    const deletePokemon = () => {
        PokemonService.deletePokemon(pokemon).then(() => history.push('/pokemons'))
    }

    return (
        <form onSubmit={e => handleSubmit(e)}>
            <div className="row">
                <div className="col s12 m8 offset-m2">
                    <div className="card hoverable"> 
                        {/* on affiche l'image que si nous sommes dans le formulaire d'édition */}
                        {isEditForm && (
                            <div className="card-image">
                                <img src={pokemon.picture} alt={pokemon.name} style={{width: '250px', margin: '0 auto'}}/>
                                <span className='btn-floating halfway-fab waves-effect waves-light'>
                                    <i onClick={deletePokemon} className="material-icons">delete</i>
                                </span>
                            </div>
                        )}
                        <div className="card-stacked">
                            <div className="card-content">
                                {/* Pokemon picture */}
                                {isAddForm() && (
                                    <div className="form-group">
                                        <label htmlFor="picture">Image</label>
                                        <input id="picture" name="picture" type="text" className="form-control" value={form.picture.value} onChange={e => handleInputChange(e)}></input>
                                        {form.picture.error &&
                                        <div className='card-panel red accent-1'>
                                            {form.picture.error}
                                        </div>
                                        }
                                    </div>
                                )}
                                {/* Pokemon name */}
                                <div className="form-group">
                                    <label htmlFor="name">Nom</label>
                                    <input id="name" name="name" type="text" className="form-control" value={form.name.value} onChange={e => handleInputChange(e)}></input>
                                    {form.name.error &&
                                    <div className='card-panel red accent-1'>
                                        {form.name.error}
                                    </div>
                                    }
                                </div>
                                {/* Pokemon hp */}
                                <div className="form-group">
                                    <label htmlFor="hp">Point de vie</label>
                                    <input id="hp" name="hp" type="number" className="form-control" value={form.hp.value} onChange={e => handleInputChange(e)}></input>
                                    {form.hp.error &&
                                    <div className='card-panel red accent-1'>
                                        {form.hp.error}
                                    </div>
                                    }
                                </div>
                                {/* Pokemon cp */}
                                <div className="form-group">
                                    <label htmlFor="cp">Dégâts</label>
                                    <input id="cp" name="cp" type="number" className="form-control" value={form.cp.value} onChange={e => handleInputChange(e)}></input>
                                    {form.cp.error &&
                                    <div className='card-panel red accent-1'>
                                        {form.cp.error}
                                    </div>
                                    }
                                </div>
                                {/* Pokemon types */}
                                <div className="form-group">
                                    <label>Types</label>
                                    {types.map(type => (
                                        <div key={type} style={{marginBottom: '10px'}}>
                                            <label>
                                                {/* checked sera true quand le type est posséder par le pokemon */}
                                                <input id={type} type="checkbox" className="filled-in" value={type} disabled={!isTypesValid(type)} checked={hasType(type)} onChange={e => selectType(type, e)}></input>
                                                <span>
                                                    <p className={formatType(type)}>{ type }</p>
                                                </span>
                                            </label>
                                        </div>
                                    ))}
                                </div>
                            </div>
                            <div className="card-action center">
                                {/* Submit button */}
                                <button type="submit" className="btn">Valider</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </form>
    );
};

export default PokemonForm;
