import React, { FunctionComponent, useState, useEffect } from 'react';
import Pokemon from './models/pokemon';
import POKEMONS from './models/mock-pokemon';

// HOOKS
// permet de gerer la valeur d'une variable dynamiquement et de gerer son cycle de vie
// Condition d'utilisation: ne pas appeler un hooks dans une boucle ou if, doit se placer à la racine dans un composant de fonction uniquement, quand on utilise set.. la nouvelle valeur écrase l'ancienne ( si on veut ajouter alors il faut créer une nvlle donnée contenant l'ancienne + la new)
// Cycle de vie d'un composant via ls hooks: 
//  - componentDidMount(): "Montage" methode appelée lors de la création du composant (lors de son insertion dans le DOM), permet de mettre en place des instruction lors de l'initialisation du composant, comme la recuperation de données depuis un serveur par exemple.
//  - componentDidUpdate(prevProps, prevState): Quand react detect que les valeurs d'une propriété du composant sont modifiées, le composant est mis à jour. 2param(1, 2) 1: propriétés / 2: l'état avant la mise à jour
//  - componentWillUnmount(): "Démontage": méthode appelée juste avant la destruction du composant suite à la navigation de l'utilisateur par exemple

const App: FunctionComponent = () => {
    // HOOK d'état
    // Etat initial "didMount"
    const [pokemons, setPokemons] = useState<Pokemon[]>([]); // Pokemon: correspond à la classe Pokemon / [] état initiale: vide

    // Etat modifié "didUpdate", 2param(1,2) = 1: fonction qui modifie le composant avec comme argument la nouvelle valeur qui sera dynamique / 2: en passant un array vide cela permet d'eviter de déclencher  le hook d'effet à chaque modification du composant
    useEffect(() => {
        setPokemons(POKEMONS);
    }, [])

    
    return (
        <div>
            <h1 className='center'>Pokedex</h1>
            <div className="container">
                <div className="row">
                    {pokemons.map((e) => (
                        <div className="col s6 m4" key={e.id}>
                            <div className="card horizontal">
                                <div className="card-image">
                                    <img src={e.picture} alt={e.name} />
                                </div>
                                <div className="card-stacked">
                                    <div className="card-content">
                                        <p>{e.name}</p>
                                        <p><small>{e.created.toString()}</small></p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <p>Il y a {pokemons.length} pokemons dans mon pokedex</p>
        </div>
    )
}
  
export default App;
