import React, { FunctionComponent, useState } from 'react';
import Pokemon from './models/pokemon';
import POKEMONS from './models/mock-pokemon';

// cree un composant App qui contient une fonction / le typage grace à TypeScript: React.FC (FC = function componant, un composant que l'on peut ecrire sous la forme d'une fonction)
const App: FunctionComponent = () => {
    // variable composé de son nom et de la fonction qui permet de modifié sa valeur dynamiquement grace au hook useState() / <String> : Typage TypeScript
    const [pokemon] = useState<Pokemon[]>(POKEMONS);

    return (
        <h1>Hello, {pokemon.length} !</h1>
    )
}
  
export default App;
